using ByondLang.Api;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace ByondLang.State
{
    public class LocalExecutionContext : IExecutionContext
    {
        Services.ProgramService programService;
        Api.StatusResponse lastStatus;

        public void Dispose()
        {
            programService.FullDispose();
        }

        public async Task ExecuteScript(string script)
        {
            await programService.execute(new ExecuteRequest()
            {
                Code = script
            }, null);
        }

