        public async Task HandleTopic(string topic, string data)
        {
            await programService.handleTopic(new TopicRequest()
            {
                TopicId = topic,
                Data = data
            }, null);
        }

        public async Task InitializeProgram(ProgramType type)
        {
            lastStatus = await programService.status(new StatusRequest()
            {
                Initialize = true,
                Type = type,
            }, null);
        }

        public Task<bool> Recycle()
        {
            programService.recycle(null, null);
            return Task.FromResult(true);
        }

        public void Start(Func<int> portGenerator, IServiceProvider serviceProvider)
        {
            programService = new Services.ProgramService(new Services.NTSL3StateService(serviceProvider));
        }
    }
}
